(2, 'wc-admin-onboarding-profiler-reminder', 'update', 'en_US', '¡Bienvenido a WooCommerce! Configura tu tienda y empieza a vender', 'Estamos aquí para ayudarte a dar los pasos más importantes para poner tu tienda en marcha.', '{}', 'actioned', 'woocommerce-admin', '2020-09-25 15:30:29', NULL, 0, 'plain', '', 0, 'info'),
(3, 'wc-admin-draw-attention', 'info', 'en_US', 'Cómo llamar la atención sobre tu tienda en línea', 'Para comenzar, aquí tienes siete maneras de aumentar tus ventas y evitar que te ahogues con productos similares producidos en masa que compiten por los mismos compradores.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-25 15:32:24', NULL, 0, 'plain', '', 1, 'info'),
(4, 'mailchimp-for-woocommerce-incomplete-install', 'warning', 'en_US', 'MailChimp para WooCommerce', 'El plugin aún no está conectado a una cuenta de Mailchimp. Para completar la conexión, abre la página de ajustes.', '{\"getting_started\":true,\"activated\":1601047978,\"activated_formatted\":\"September 25th\"}', 'unactioned', 'mailchimp-for-woocommerce', '2020-09-25 15:32:58', NULL, 0, 'plain', '', 1, 'info'),
(5, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', '{}', 'pending', 'woocommerce.com', '2020-09-26 03:15:12', NULL, 0, 'plain', '', 0, 'info'),
(6, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Services', 'WooCommerce Services helps get your store “ready to sell” as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', '{}', 'pending', 'woocommerce.com', '2020-09-26 03:15:12', NULL, 0, 'plain', '', 0, 'info'),
(7, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.', '{}', 'pending', 'woocommerce.com', '2020-09-26 03:15:12', NULL, 0, 'plain', '', 0, 'info');
